import React, {useState} from 'react';
import users from '../users.json';


const UsersBox = () => {

    const [index ,setindex]= useState(0)

    const ChageUser = () => setindex(index+1)
        if (index == users.length) {
            alert("ya recorriste todos los usuarios" )
        }
    
       
            
        

        
    
   
    return (
        <div>
            <h1> {users[index].name.title} {users[index].name.first} {users[index].name.last} </h1>
         
            <img src={users[index].picture.medium}/>

            
           <div><button onClick={ChageUser} >Cambiar usuario</button></div>
    
        </div>
    );
};

export default UsersBox;
